import { createSlice } from '@reduxjs/toolkit';

export const cartSlice = createSlice({
  name: 'cart',
  initialState: { listCart: [] },
  reducers: {
    ADD_CART: (state, actions) => {
      state.listCart = state.listCart.concat(actions.payload);
      console.log(state.listCart);
    },
    UPDATE_CART: (state, actions) => {
      const updateItem = state.listCart.filter(
        (item) => item._id === actions.payload._id
      );
      console.log(state.listCart.map((el) => el._id));
      console.log(actions.payload._id);
      console.log(updateItem);
    },
    DELETE_CART: (state, actions) => {
      console.log(state.listCart);
    },
  },
});

export const { ADD_CART, UPDATE_CART, DELETE_CART } = cartSlice.actions;

export default cartSlice.reducer;
