import React from 'react';
import { useDispatch } from 'react-redux';
import { hidePopUp } from '../store/popup';

import classes from './Popup.module.css';

const Popup = (props) => {
  //Dispatch actions của store
  const dispatch = useDispatch();

  //Tắt pop up
  const closeHandler = () => {
    dispatch(hidePopUp());
  };

  return (
    <div className={classes.popup}>
      <img src='img/product_1.png' alt=''></img>
      <button className={classes.close} onClick={closeHandler}>
        &times;
      </button>
      <div className={classes.popup_text}>
        <h2>Apple Iphone</h2>
        <p>29,990,000 VND</p>
        <p>Lorednfklanslninadnsflk</p>
        <button>
          <i className='fa fa-shopping-cart'></i>View Detail
        </button>
      </div>
    </div>
  );
};

export default Popup;
