import React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { DELETE_CART } from '../store/cart';

import Footer from '../UI/Footer';
import NavBar from '../UI/NavBar';

import classes from './Cart.module.css';

const Cart = () => {
  //State
  const [product, setProduct] = useState([]);
  const [loaded, setLoaded] = useState(false);

  //State của cart list
  const cartList = useSelector((state) => state.cart.listCart);
  //Dispatch actions
  const dispatch = useDispatch();

  //Fetch sản phẩm
  const fetchProductId = async () => {
    try {
      const response = await fetch(
        'https://firebasestorage.googleapis.com/v0/b/funix-subtitle.appspot.com/o/Boutique_products.json?alt=media&token=dc67a5ea-e3e0-479e-9eaf-5e01bcd09c74'
      );
      const data = await response.json();
      setProduct(data);
      setLoaded(true);
    } catch (error) {
      throw new Error('Request failed!');
    }
  };

  useEffect(() => {
    fetchProductId();
  }, []);

  //Xóa sản phẩm
  const deleteCartHandler = (item) => {
    dispatch(DELETE_CART(item));
  };

  return (
    <div>
      <NavBar />
      <div className={classes.cart}>
        <div className={classes.cart_banner}>
          <header>CART</header>
          <p>CART</p>
        </div>
        <h1>SHOPPING CART</h1>
        <table className={classes.cart_items}>
          <thead>
            <tr>
              <th>IMAGE</th>
              <th>PRODUCT</th>
              <th>PRICE</th>
              <th>QUANTITY</th>
              <th>TOTAL</th>
              <th>REMOVE</th>
            </tr>
          </thead>
          <tbody>
            {loaded &&
              cartList.map((items) => {
                return (
                  <tr key={items._id}>
                    <td>
                      <img src={items.img1} className={classes.cart_imgs}></img>
                    </td>
                    <td>{items.name}</td>
                    <td>
                      {items.price.replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')}{' '}
                      VND
                    </td>
                    <td>
                      <button>
                        <i className='fas fa-caret-left'></i>
                      </button>
                      {items.quantity}
                      <button>
                        <i className='fas fa-caret-right'></i>
                      </button>
                    </td>
                    <td>
                      {items.price.replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')}{' '}
                      VND
                    </td>
                    <td>
                      <i
                        className='far fa-trash-alt'
                        onClick={() => deleteCartHandler(items)}
                      ></i>
                    </td>
                  </tr>
                );
              })}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan='6'>
                <button id={classes.left}>
                  <i className='fas fa-long-arrow-alt-left'></i>
                  {} Continue shopping
                </button>
                <button id={classes.right}>
                  Proceed to checkout {}
                  <i className='fas fa-long-arrow-alt-right'></i>
                </button>
              </td>
            </tr>
          </tfoot>
        </table>
        <div className={classes.cart_total}>
          <h2>CART TOTAL</h2>
          <h3>SUBTOTAL</h3>
          <p>19.779.000 VND</p>
          <hr></hr>
          <h3>TOTAL</h3>
          <p id={classes.price_total}>19.779.000 VND</p>
          <form className={classes.coupon}>
            <input placeholder='Enter your coupon'></input>
            <button>
              <i className='fas fa-gift	'></i> Apply coupon
            </button>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Cart;
