import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router';
import { DELETE_CART } from '../store/cart';

import Footer from '../UI/Footer';
import NavBar from '../UI/NavBar';

import classes from './Cart.module.css';

const Cart = () => {
  //State của cart list
  const cartList = useSelector((state) => state.cart.listCart);
  //Dispatch actions
  const dispatch = useDispatch();

  //Xóa sản phẩm
  const deleteCartHandler = (item) => {
    //Tìm vị trí của item trong state listCart
    const index = cartList.indexOf(item);
    //Dispatch action xóa
    dispatch(DELETE_CART(index));
  };

  //Chuyển về trang shop
  const navigate = useNavigate();

  //Function tính tổng giá tiền
  const totalPrice = (price, quantity) => {
    const total = price * quantity;
    return total.replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,');
  };

  return (
    <div>
      <NavBar />
      <div className={classes.cart}>
        <div className={classes.cart_banner}>
          <header>CART</header>
          <p>CART</p>
        </div>
        <h1>SHOPPING CART</h1>
        <table className={classes.cart_items}>
          <thead>
            <tr>
              <th>IMAGE</th>
              <th>PRODUCT</th>
              <th>PRICE</th>
              <th>QUANTITY</th>
              <th>TOTAL</th>
              <th>REMOVE</th>
            </tr>
          </thead>
          <tbody>
            {cartList.map((items) => {
              return (
                <tr>
                  <td>
                    <img src={items.img1} className={classes.cart_imgs}></img>
                  </td>
                  <td className={classes.name}>{items.name}</td>
                  <td>
                    {items.price.replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')} VND
                  </td>
                  <td>
                    <button>
                      <i className='fas fa-caret-left'></i>
                    </button>
                    {items.quantity}
                    <button>
                      <i className='fas fa-caret-right'></i>
                    </button>
                  </td>
                  <td>
                    {totalPrice(items.price, items.quantity)}
                    VND
                  </td>
                  <td>
                    <i
                      className='far fa-trash-alt'
                      onClick={() => deleteCartHandler(items)}
                    ></i>
                  </td>
                </tr>
              );
            })}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan='6'>
                <button
                  id={classes.left}
                  onClick={() => {
                    navigate('/shop');
                  }}
                >
                  <i className='fas fa-long-arrow-alt-left'></i>
                  {} Continue shopping
                </button>
                <button id={classes.right}>
                  Proceed to checkout {}
                  <i className='fas fa-long-arrow-alt-right'></i>
                </button>
              </td>
            </tr>
          </tfoot>
        </table>
        <div className={classes.cart_total}>
          <h2>CART TOTAL</h2>
          <h3>SUBTOTAL</h3>
          <p>19.779.000 VND</p>
          <hr></hr>
          <h3>TOTAL</h3>
          <p id={classes.price_total}>19.779.000 VND</p>
          <form className={classes.coupon}>
            <input placeholder='Enter your coupon'></input>
            <button>
              <i className='fas fa-gift	'></i> Apply coupon
            </button>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Cart;
