import { createSlice } from '@reduxjs/toolkit';

export const popupSlice = createSlice({
  name: 'popup',
  initialState: { popup: false, items: '', login: false },
  reducers: {
    showPopUp: (state, actions) => {
      state.popup = true;
      state.items = actions.payload;
    },
    hidePopUp: (state) => {
      state.popup = false;
      state.items = '';
    },
    logIn: (state) => {
      state.login = true;
    },
  },
});

export const { showPopUp, hidePopUp, logIn } = popupSlice.actions;

export default popupSlice.reducer;
