import { combineReducers } from 'redux';

//Reducer
export const popupReducer = (state = false, action) => {
  switch (action.type) {
    case 'SHOW_POPUP':
      return console.log('true');
    case 'HIDE_POPUP':
      return console.log('false');
    default:
      return state;
  }
};

const allReducers = combineReducers({ popupReducer });

export default allReducers;
