import { createSlice } from '@reduxjs/toolkit';

export const popupSlice = createSlice({
  name: 'popup',
  initialState: { toggle: false },
  reducers: {
    showPopUp: (state) => {
      state = true;
    },
    hidePopUp: (state) => {
      state = false;
    },
  },
});

export const { showPopUp, hidePopUp } = popupSlice.actions;

export default popupSlice.reducer;
