import { createSlice } from '@reduxjs/toolkit';

export const popupSlice = createSlice({
  name: 'popup',
  initialState: { popup: false },
  reducers: {
    showPopUp: (state) => {
      state.popup = true;
    },
    hidePopUp: (state) => {
      state.popup = false;
    },
  },
});

export const { showPopUp, hidePopUp } = popupSlice.actions;

export default popupSlice.reducer;
