import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';

import Footer from '../UI/Footer';
import NavBar from '../UI/NavBar';

// import classes from './Detail.module.css';

const DetailPage = () => {
  //ID của product
  let { productId } = useParams();

  //State
  const [category, setCategory] = useState([]);
  const [loaded, setLoaded] = useState(false);

  //Fetch sản phẩm
  const fetchProductId = async (id) => {
    try {
      const response = await fetch(
        'https://firebasestorage.googleapis.com/v0/b/funix-subtitle.appspot.com/o/Boutique_products.json?alt=media&token=dc67a5ea-e3e0-479e-9eaf-5e01bcd09c74'
      );
      const data = await response.json();
      const result = await data.filter((items) => items._id.$oid === id);
      setCategory(result);
      setLoaded(true);
    } catch (error) {
      throw new Error('Request failed!');
    }
  };

  useEffect(() => {
    fetchProductId(productId);
  }, [productId]);

  return (
    <div>
      <NavBar />
      {loaded && (
        <div>
          <img src={category[0].img1} />
          <img src={category[0].img2} />
          <img src={category[0].img3} />
          <img src={category[0].img4} />
          <header>{category[0].name}</header>
          <p>{items.price.replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')} VND</p>
        </div>
      )}
      <Footer />
    </div>
  );
};

export default DetailPage;
