import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { hidePopUp, showPopUp } from '../../store/popup';

import classes from './Products.module.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loaded, setLoaded] = useState(false);

  //Fetch sản phẩm
  const fetchProducts = async () => {
    try {
      const response = await fetch(
        'https://firebasestorage.googleapis.com/v0/b/funix-subtitle.appspot.com/o/Boutique_products.json?alt=media&token=dc67a5ea-e3e0-479e-9eaf-5e01bcd09c74'
      );
      const data = await response.json();
      setProducts(data);
      setLoaded(true);
    } catch (error) {
      throw new Error('Request failed!');
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const popup = useSelector((state) => state.popup.popup);
  const dispatch = useDispatch();

  return (
    <>
      <div className={classes.products_text}>
        <p>MADE THE HARD WAY</p>
        <h1>TOP TRENDING PRODUCTS</h1>
      </div>
      <div>
        {loaded &&
          products.map((items) => {
            return (
              <div key={items._id.$oid} className={classes.products}>
                <img
                  src={items.img1}
                  alt={items.name}
                  onClick={() => {
                    !popup ? dispatch(showPopUp) : dispatch(hidePopUp);
                  }}
                />
                <header>{items.name}</header>
                <p>
                  {items.price.replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')} VND
                </p>
              </div>
            );
          })}
      </div>
    </>
  );
};

export default Products;
