import React from 'react';
import { Link } from 'react-router-dom';

import Footer from '../../UI/Footer';
import NavBar from '../../UI/NavBar';

import classes from './SignUp.module.css';

const SignUp = () => {
  //Thông tin người dùng
  const userArr = {
    name: '',
    email: '',
    password: '',
    phone: '',
  };

  //Authentication form
  const formSubmit = (event) => {
    event.preventDefault();
    alert('Submit!');
  };

  return (
    <div>
      <NavBar />
      <div className={classes.signup}>
        <div className={classes.card}>
          <h1>Sign Up</h1>
          <form>
            <input type='name' placeholder='Full Name' id='name'></input>
            <input type='email' placeholder='Email' id='mail'></input>
            <input type='password' placeholder='Password' id='password'></input>
            <input type='phone' placeholder='Phone' id='phone'></input>
            <button onClick={formSubmit}>SIGN UP</button>
          </form>
          <p>
            Login?
            <Link to='/login'>Click</Link>
          </p>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default SignUp;
